// Solution 1
/*
 * N = str1, M = str2
 * Time: O(N + M)
 * Additional space: O(N)
 */
function checkPermutation(str1, str2) {
  if (str1.length !== str2.length) return false;
  const list = new Map();
  for (const letter of str1) {
    if (list.has(letter)) {
      const val = list.get(letter);
      list.set(letter, val + 1);
    } else {
      list.set(letter, 1);
    }
  }
  for (const letter of str2) {
    if (list.has(letter)) {
      let val = list.get(letter);
      list.set(letter, val - 1);
      val = list.get(letter);
      if (val === 0) list.delete(letter);
    } else {
      return false;
    }
  }
  return list.size === 0;
}
/**
 * Sort both strings and check if they are equal afterwards. Permutations will
 * be identical sorted strings.
 *
 * N = |str1| && M = |str2|
 * Time: O(N lg N + M lg M)
 * Additional space: O(1) if able to modify original strings, O(N + M) otherwise
 */
function isPermutationSorted(str1, str2) {
  if (str1.length === 0 || str1.length !== str2.length) {
    return false;
  }
  const arr1 = str1.split('');
  const arr2 = str2.split('');
  arr1.sort();
  arr2.sort();
  return arr1.every((v, i) => v === arr2[i]);
}

