            if (matrix[y][x] === 0) {
                rows[y] = true;
                cols[x] = true;
            }
        }
    }
    for (let y = 0; y < rows.length; ++y) {
        for (let x = 0; x < cols.length; ++x) {
            if (rows[y] || cols[x]) {
                matrix[y][x] = 0;
            }
        }
    }
    return matrix;
}

