    if (dp[index] !== undefined) return dp[index];
    var str = '';
    var res = false;
    var len = s.length;
    if (index === len) return true;
    for (var i = index; i < len; i++) {
        str = s.substring(index, i + 1);
        if (map[str] && find(s, map, dp, i + 1)) {
        res = true;
        break;
        }
    }
    dp[index] = res;
    return res;
};

