function zeroMatrix(array) {
    const matrix = JSON.parse(JSON.stringify(array));
    let rows = new Array(matrix.length).fill(false);
    let cols = new Array(matrix[0].length).fill(false);
    for (let y = 0; y < rows.length; ++y) {
        for (let x = 0; x < cols.length; ++x) {
            if (matrix[y][x] === 0) {
                rows[y] = true;
                cols[x] = true;
            }
        }
    }
    for (let y = 0; y < rows.length; ++y) {
        for (let x = 0; x < cols.length; ++x) {
            if (rows[y] || cols[x]) {
                matrix[y][x] = 0;
            }
        }
    }
    return matrix;
}

