// Solution 1
function mergeSortedArray(nums1, m, nums2, n) {
    let first = m - 1;
    let second = n - 1;
    let i = m + n - 1;
    while (second >= 0) {
        let fVal = nums1[first];
        let sVal = nums2[second];
        if (fVal > sVal) {
            nums1[i] = fVal;
            i--;
            first--;
        } else {
            nums1[i] = sVal;
            i--;
            second--;
        }
    }
    return nums1;
}
// Solution 2
function mergeSortedArray(nums1, m, nums2, n) {
    let p1 = m - 1;
    let p2 = n - 1;
    let p3 = m + n - 1;
    while (p2 >= 0) {
        nums1[p3--] = p1 >= 0 && nums1[p1] > nums2[p2] ? nums1[p1--] : nums2[p2--];
    }
}

