function thirdMaximumNumber(nums) {
    let min = -Infinity;
    let mid = -Infinity;
    let max = -Infinity;
    for (const num of nums) {
        if (num > max) {
            min = mid;
            mid = max;
            max = num;
        }
        if (num < max && num > mid) {
            min = mid;
            mid = num;
        }
        if (num < mid && num > min) {
            min = num;
        }
    }
    return min > -Infinity ? min : max;
}

