function findSmallestDifference(A, B) {
    A.sort((a, b) => a - b);
    B.sort((a, b) => a - b);
    let a = 0, b = 0;
    let result = Number.MAX_SAFE_INTEGER;
    while (a < A.length && b < B.length) {
        if (Math.abs(A[a] - B[b]) < result) {
            result = Math.abs(A[a] - B[b]);
        }
        A[a] < B[b] ? a++ : b++;
    }
    return result;
}

