function productExceptSelf(nums) {
  var len = nums.length;
  var left = Array(len + 1);
  var right = Array(len + 1);
  var res = Array(len);
  left[0] = 1;
  right[0] = 1;
  for (var i = 0; i < len; i++) {
    left[i + 1] = left[i] * nums[i];
  }
  for (var j = 0; j < len; j++) {
    right[j + 1] = right[j] * nums[len - 1 - j];
  }
  for (var k = 0; k < len; k++) {
    res[k] = left[k] * right[len - k - 1];
  }
  return res;
};
// Solution 2
// Time complexity : O(n).
// Space complexity : O(1).
function productExceptSelf(nums) {
  var len = nums.length;
  var res = Array(len);
  var right = 1;
  res[0] = 1;
  for (var i = 1; i < len; i++) {
    res[i] = res[i - 1] * nums[i - 1];
  }
  for (var j = len - 1; j >= 0; j--) {
    res[j] *= right;
    right *= nums[j];
  }
  return res;
};

