        }
        this._array.splice(index, 0, value);
        ++this.length[stack - 1];
    }
    pop(stack) {
        let index = 0;
        for (let i = 0; i < stack - 1; i++) {
            index += this.length[i];
        }
        this._array.splice(index, 1);
        --this.length[stack - 1];
    }
    peek(stack) {
        let index = 0;
        for (let i = 0; i < stack - 1; i++) {
            index += this.length[i];
        }
        return this._array[index];
    }
    isEmpty(stack) {
        return this.length[stack - 1] === 0;
    }
    getStack() {
        return this._array;
    }
}

