class SetOfStacks {
    constructor(threshold) {
        this.stacks = [];
        this.threshold = threshold;
    }
    push(value) {
        if (!this.stacks.length) {
            this.stacks.push([]);
        }
        if (this.stacks[this.stacks.length - 1].length === this.threshold) {
            this.stacks.push([]);
        }
        this.stacks[this.stacks.length - 1].push(value);
    }
    pop() {
        this.stacks[this.stacks.length - 1].pop();
        if (this.stacks.length > 1 && !this.stacks[this.stacks.length - 1].length) {
            this.stacks.pop();
        }
    }
    popAt(i) {
        const index = i - 1;
        this.stacks[index].pop();
        for (let i = index + 1; i < this.stacks.length - 1; i++) {
