    var prev = null;
    // To store previous of slow_ptr
    while (fast_ptr != null && fast_ptr.next != null) {
        fast_ptr = fast_ptr.next.next;
        prev = slow_ptr;
        slow_ptr = slow_ptr.next;
    }
    // Delete the middle node
    prev.next = slow_ptr.next;
    return head;
};

