// depth first
// n = number of nodes
// e = number edges
// Time: O(e)
// Space: O(n)
function path(graph, src, dst, visited) {
    if (src === dst) return true;
    if (visited.has(src)) return false;
    visited.add(src);
    for (let neighbor of graph[src]) {
        if (path(graph, neighbor, dst, visited) === true) {
            return true;
        }
    }
    return false;
}
// breadth first
// n = number of nodes
// e = number edges
// Time: O(e)
// Space: O(n)
// JavaScript shift() methods runs in linear time, so the complexity is really O(e^2).
function path(graph, src, dst) {
    const visited = new Set([ src ]);
    const queue = [src];
    while (queue.length) {
        const current = queue.shift();
        if (current === dst) return true;
        for (let neighbor of graph[current]) {
            if (!visited.has(neighbor)) {
                visited.add(neighbor);
                queue.push(neighbor);
            }

        }
    }
    return false;
};
const buildGraph = (edges) => {
    const graph = {};
    for (let edge of edges) {
        const [ a, b ] = edge;
        if (!(a in graph)) graph[a] = [];
        if (!(b in graph)) graph[b] = [];
        graph[a].push(b);
        graph[b].push(a);
    }
    return graph;
};

